class Smartphone:                                         # Объявляем класс Smartphone 
    def __init__(self, brand, model, phone_number):       # Определяем конструктор класса 
        self.brand = brand                                # Сохраняем переданную марку в атрибут объекта self.brand
        self.model = model                                # Сохраняем переданную модель в атрибут объекта self.model
        self.phone_number = phone_number                  # Сохраняем переданный номер в атрибут объекта self.phone_number